mapper.delete:
DELETE
FROM
    t_enterprise_log
WHERE
    enterprise_id = #{enterpriseId}
AND building_id = #{buildingId}

mapper.insert:
insert into t_enterprise_log (id,
	enterprise_id,major_business, address, address_modify_flag, 
	major_business_modify_flag, building_id,community_id,street_id,
	version_id
)
values (#{id},
  #{enterpriseId},#{major_business}, #{address}, #{address_modify_flag}, 
  #{major_business_modify_flag},#{building_id},#{communityId},#{streetId},
  #{version_id}
  )

mapper.select:
	select 
	
	from t_enterprise_log 
	where 1=1
	
		and enterprise_id=#{enterpriseId}
	
	
		and building_id=#{building_id}
	
	
		and street_id=#{streetId}
	
	
		and community_id=#{communityId}
	
  
		and community_id=#{communityId}
	
  
		and address=#{address}

mapper.update:
UPDATE t_enterprise_log
SET building_id  = #{buildingId} ,street_id =#{streetId},community_id = #{communityId}
where id = (select source_log_id from t_enterprise_base where id = #{enterpriseId})
